package io.github.ajoz.aoc16

import io.kotlintest.matchers.shouldBe
import io.kotlintest.specs.WordSpec

class Day2Part2PuzzleTest : WordSpec() {
    init {
        "Code to the toilet in the Bunny HQ" should {
            "be 5DB3 for the given short instructions" {
                val instructions = """
                |ULL
                |RRDDD
                |LURDL
                |UUUUD""".trimMargin()

                getPart2BathroomAccessCode(instructions) shouldBe "5DB3"
            }
        }

        "be D65C3 for the given long instructions" {
            val instructions = """
|
|
|
|
|
                """.trimMargin()

            getPart2BathroomAccessCode(instructions) shouldBe "D65C3"
        }
    }
}